import React, { Component } from "react";

import Container from "react-bootstrap/Container";
import Jumbotron from "react-bootstrap/Jumbotron";
import Form from "react-bootstrap/Form";

import './App.css';

class App extends Component {
  //code
  constructor(props) {
    super(props);

    this.state = {
      date: "",
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  //
  render() {
    return (
      <div className="App">
        <Container className="p-3 text-left">
          <Jumbotron >
            <div>
              <h1 className="font-weight-light text-primary">
                Digitickets Text Input
              </h1>
              <h2 className="font-weight-light text-primary">
                Please enter a valid historical date using the format DD/MM/YYYY
              </h2>
            </div>
            <div>
              <Form>
                <Form.Group 
                  controlId="formBasicTextInput"
                  >
                  <Form.Label
                    >Date
                  </Form.Label>
                  <Form.Control 
                    type="email" 
                    placeholder="DD/MM/YYYY" 
                    size="lg"
                  />
                  <Form.Text className="text-muted">
                    Helper Text
                  </Form.Text>
                </Form.Group>
              </Form>
            </div>
          </Jumbotron>
        </Container>
      </div>
    );
  }
}

export default App;
