import React, { Component } from "react";

import Container from "react-bootstrap/Container";
import Jumbotron from "react-bootstrap/Jumbotron";
import Form from "react-bootstrap/Form";

import './App.css';

class App extends Component {
  //code
  // http://michaelsoriano.com/react-bootstrap-form-validation/
  constructor(props) {
    super(props);

    this.state = {
      date: "",
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  hasError(date) {
    return true;
    // return true/false if date valid?
    // return this.state.errors.indexOf(key) !== -1;
  }
  handleInputChange(event) {
    console.log("CHNAGE", event.target.name)
    var key = event.target.name;
    var value = event.target.value;
    var obj = {};
    obj[key] = value;
    this.setState(obj);
  }

  //
  render() {
    return (
      <div className="App">
        <Container className="p-3 text-left">
          <Jumbotron >
            <div>
              <h1 className="font-weight-light text-primary">
                Digitickets Text Input
              </h1>
              <h2 className="font-weight-light text-primary">
                Please enter a valid historical date using the format DD/MM/YYYY
              </h2>
            </div>
            <div>
              <Form>
                <Form.Group 
                  controlId="formBasicTextInput"
                  >
                  <Form.Label
                    >Date
                  </Form.Label>
                  <Form.Control 
                    type="email" 
                    placeholder="DD/MM/YYYY" 
                    size="lg"
                    className={
                    this.hasError("firstname")
                      ? "form-control is-invalid"
                      : "form-control"
                    }
                    onChange={this.handleInputChange}
                  />
                  <Form.Text className="text-muted">
                    Helper Text
                  </Form.Text>
                </Form.Group>
              </Form>
            </div>
          </Jumbotron>
        </Container>
      </div>
    );
  }
}

export default App;
